def self.parse grammar
  grammar=grammar.split(" ")
  result = ""
  grammar.each do |argument|
    if(argument =~ /^\d.*%/)
      d= argument.split("%#");
      result += percent_parse(d[0],d[1])
    elsif(argument =~ /^</)
      result += max_parse(argument[1],argument[3,argument.size])
    else
      result += normal_parse(argument)
    end
  end
  result
end

def self.percent_parse percent,type
  if chance(percent)
    parse(element(type))
  else
    ""
  end
end

def self.max_parse max,type
  result = ""
  Random.rand(max.to_i).times do
    result += parse(element(type))
  end
  result
end

def self.normal_parse word
  if word =~ /^#/
    element(word.sub(/#/,''))
  else
    word + " "
  end
end

def self.element(variable)
  instance_variable_get("@#{variable}").sample + " "
end

def self.chance(int)
  Random.rand(100) < int.to_i
end

def self.gonzo
  parse @grammars[:gonzo][:grammar]
end

def self.hollywood
  parse @grammars[:hollywood][:grammar]
end

def self.netflix
  parse @grammars[:netflix][:grammar]
end

